DROP DATABASE if exists ig_clone;
CREATE DATABASE ig_clone;
USE ig_clone;


CREATE TABLE photos
(
photo_id INT NOT NULL, /*Primary ID that preferably auto increments (if supported in chosen DB)*/

user_id INT NOT NULL, /*ID of the user who owns this photo (Indexed field)*/

caption varchar(256) NOT NULL, /*Photo caption*/

latitude float, /*Latitude value for location*/

longitude float, /*Longitude value location*/
image_path text NOT NULL, /*Path to image on server*/

image_size INT NOT NULL, /*Image size on server*/

date_created DATETIME, /*When was this image created?*/

date_updated   DATETIME,/*Last time*/ 
PRIMARY KEY(photo_id)
);
CREATE TABLE users(
user_id INT  NOT NULL, /*Primary ID that preferably auto increments (if supported in chosen DB)*/

username varchar(25) NOT NULL, /* Username (Unique Index)*/

email char(125) UNIQUE NOT NULL,/*(String), Email address (Unique Index)*/

salted_password varchar(15) NOT NULL,  /*(String), Salted password digest*/

first_name varchar(25) NOT NULL, /*(String), First name of user*/

last_name varchar(25) NOT NULL, /*(String), Last name of user*/

last_ip varchar(15) NOT NULL, /*(String), Last known user IP address*/

date_created DATETIME, /*(Unix Timestamp or DateTime), When did this user sign up?*/

date_updated DATETIME,/*(Unix Timestamp or DateTime), Last time this user was updated?*/
PRIMARY KEY(user_id)
);

CREATE TABLE messages(
message_id INT,
user_id INT,
photo_id INT,
message_content varchar(500),
FOREIGN KEY (user_id) REFERENCES users(user_id),
FOREIGN KEY (photo_id) REFERENCES photos(photo_id),
PRIMARY KEY (message_id)
);
CREATE TABLE album(
album_id INT NOT NULL,
title char(15) NOT NULL,
photo_id INT(5) NOT NULL,
FOREIGN KEY (photo_id) REFERENCES photos(photo_id)
-- PRIMARY KEY(album_id)
);
CREATE TABLE IF NOT EXISTS `ig_clone`.`photos_has_messages` (
  `photos_photo_id` INT(11) NOT NULL,
  `messages_message_id` INT(11) NOT NULL,
  PRIMARY KEY (`photos_photo_id`, `messages_message_id`),
  INDEX `fk_photos_has_messages_messages1_idx` (`messages_message_id` ASC) VISIBLE,
  INDEX `fk_photos_has_messages_photos1_idx` (`photos_photo_id` ASC) VISIBLE,
  CONSTRAINT `fk_photos_has_messages_photos1`
    FOREIGN KEY (`photos_photo_id`)
    REFERENCES `ig_clone`.`photos` (`photo_id`)
    ON DELETE NO ACTION
    ON UPDATE NO ACTION,
  CONSTRAINT `fk_photos_has_messages_messages1`
    FOREIGN KEY (`messages_message_id`)
    REFERENCES `ig_clone`.`messages` (`message_id`)
    ON DELETE NO ACTION
    ON UPDATE NO ACTION
    );

ALTER TABLE photos ADD FOREIGN KEY (user_id) REFERENCES users(user_id);
ALTER TABLE `messages` ADD `message_content` TEXT NOT NULL AFTER `message_id`;
ALTER TABLE `photos` CHANGE `image_path` `image_path` TEXT CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL;

